import React, { useState, useEffect } from 'react';
import { View, StyleSheet, FlatList, TouchableOpacity, Alert } from 'react-native';
import { 
  Text, 
  Card, 
  FAB, 
  ActivityIndicator, 
  TextInput, 
  IconButton, 
  Portal, 
  Dialog, 
  Button, 
  Divider 
} from 'react-native-paper';
import { SafeAreaView } from 'react-native-safe-area-context';
import { conversationApi } from '../../utils/api';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { useTheme } from '../../utils/ThemeContext';
import { useFocusEffect } from '@react-navigation/native';
import QuickMenu from '../../components/QuickMenu';
import Icon from 'react-native-vector-icons/MaterialCommunityIcons';

const ConversationListScreen = ({ navigation }) => {
  const { theme } = useTheme();
  const { colors, spacing, fonts, shadows, borderRadius } = theme;
  
  // State variables
  const [conversations, setConversations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [filteredConversations, setFilteredConversations] = useState([]);
  const [selectedConversation, setSelectedConversation] = useState(null);
  const [menuVisible, setMenuVisible] = useState(false);
  const [menuPosition, setMenuPosition] = useState({ right: 10, top: 50 });
  const [renameDialogVisible, setRenameDialogVisible] = useState(false);
  const [newTitle, setNewTitle] = useState('');
  
  // Move the styles definition here, before they're used in any conditionals
  const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: colors.background,
    },
    centered: {
      flex: 1,
      justifyContent: 'center',
      alignItems: 'center',
      padding: spacing.l,
    },
    searchContainer: {
      margin: spacing.m,
    },
    searchBar: {
      backgroundColor: colors.surface,
      borderRadius: borderRadius.large,
      height: 50,
      ...shadows.small,
    },
    listContent: {
      padding: spacing.m,
      paddingBottom: spacing.xxxl, // Extra padding for FAB
    },
    card: {
      marginBottom: spacing.m,
      backgroundColor: colors.card,
      borderRadius: borderRadius.large,
      overflow: 'hidden',
      ...shadows.medium,
      borderWidth: 1,
      borderColor: colors.border,
    },
    conversationTitle: {
      ...fonts.semiBold,
      fontSize: fonts.sizes.large,
      marginBottom: spacing.xs,
      color: colors.text,
    },
    lastMessage: {
      ...fonts.regular,
      color: colors.textSecondary,
      marginBottom: spacing.s,
      lineHeight: 20,
    },
    timestamp: {
      ...fonts.medium,
      color: colors.textSecondary,
      fontSize: fonts.sizes.xs,
      alignSelf: 'flex-end',
    },
    errorText: {
      color: colors.error,
      textAlign: 'center',
      marginBottom: spacing.m,
    },
    retryButton: {
      padding: spacing.m,
      backgroundColor: colors.primary,
      borderRadius: borderRadius.medium,
    },
    retryText: {
      color: '#fff',
      ...fonts.medium,
    },
    emptyContainer: {
      flex: 1,
      justifyContent: 'center',
      alignItems: 'center',
      padding: spacing.l,
    },
    emptyText: {
      ...fonts.medium,
      fontSize: fonts.sizes.large,
      marginBottom: spacing.s,
      color: colors.text,
    },
    emptySubtext: {
      color: colors.textSecondary,
      textAlign: 'center',
    },
    fab: {
      position: 'absolute',
      margin: spacing.m,
      right: 0,
      bottom: 0,
      backgroundColor: colors.primary,
      borderRadius: borderRadius.round,
      ...shadows.large,
      width: 60,
      height: 60,
    },
    dialogInput: {
      backgroundColor: colors.surface,
      borderRadius: borderRadius.medium,
      marginTop: spacing.s,
      borderColor: colors.border,
    },
    dialogTitle: {
      ...fonts.semiBold,
      color: colors.text,
    },
    dialogButton: {
      borderRadius: borderRadius.medium,
      paddingHorizontal: spacing.m,
    },
    cardActions: {
      position: 'absolute',
      top: spacing.xs,
      right: spacing.xs,
      zIndex: 1,
    },
  });
  
  // Load conversations when component mounts
  useEffect(() => {
    fetchConversations();
  }, []);

  // Refresh conversations when screen comes into focus
  useFocusEffect(
    React.useCallback(() => {
      fetchConversations();
    }, [])
  );

  // Filter conversations when search query changes
  useEffect(() => {
    if (searchQuery && conversations.length > 0) {
      const filtered = conversations.filter(convo => 
        convo.title.toLowerCase().includes(searchQuery.toLowerCase())
      );
      setFilteredConversations(filtered);
    } else {
      setFilteredConversations(conversations);
    }
  }, [searchQuery, conversations]);

  const fetchConversations = async () => {
    setLoading(true);
    try {
      const response = await conversationApi.getAll();
      setConversations(response.data.conversations || []);
      setFilteredConversations(response.data.conversations || []);
      setError(null);
    } catch (error) {
      console.error('Error fetching conversations:', error);
      setError('Failed to load conversations. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleRenameConversation = async () => {
    if (!newTitle.trim() || !selectedConversation) return;
    
    try {
      // Make API call to update the conversation
      await conversationApi.update(selectedConversation.id, {
        title: newTitle.trim()
      });
      
      // Create a copy of the conversations array with the updated title
      const updatedConversations = conversations.map(conv => 
        conv.id === selectedConversation.id 
          ? { ...conv, title: newTitle.trim() } 
          : conv
      );
      
      // Update state with the new conversations array
      setConversations(updatedConversations);
      
      // Also update filtered conversations if search is active
      if (searchQuery) {
        const updatedFiltered = filteredConversations.map(conv => 
          conv.id === selectedConversation.id 
            ? { ...conv, title: newTitle.trim() } 
            : conv
        );
        setFilteredConversations(updatedFiltered);
      }
      
      // Close dialog and reset
      setRenameDialogVisible(false);
      setSelectedConversation(null);
      setNewTitle('');
    } catch (error) {
      console.error('Error renaming conversation:', error);
      Alert.alert('Error', 'Failed to rename conversation');
    }
  };
  
  const handleIconPress = (item, event) => {
    if (event && event.nativeEvent) {
      // Get the position of the press to position the menu
      const { pageY, pageX } = event.nativeEvent;
      setMenuPosition({ 
        right: 10, // Keep the menu on the right side
        top: pageY // Position at the Y coordinate of the press
      });
    }
    setSelectedConversation(item);
    setMenuVisible(true);
  };
  
  const renderConversationItem = ({ item }) => {
    const lastMessage = item.messages && item.messages.length > 0 
      ? item.messages[item.messages.length - 1] 
      : null;
    
    const formattedDate = new Date(item.updatedAt).toLocaleDateString();
    
    return (
      <TouchableOpacity
        onPress={() => navigation.navigate('ConversationDetail', { 
          conversationId: item.id,
          title: item.title
        })}
        onLongPress={() => handleIconPress(item, null)}
        delayLongPress={500}
      >
        <Card style={styles.card}>
          <View style={styles.cardActions}>
            <IconButton
              icon="dots-vertical"
              size={20}
              onPress={(event) => handleIconPress(item, event)}
              color={colors.textSecondary}
            />
          </View>
          <Card.Content>
            <Text style={styles.conversationTitle}>{item.title}</Text>
            {lastMessage && (
              <Text style={styles.lastMessage} numberOfLines={2}>
                {lastMessage.content}
              </Text>
            )}
            <Text style={styles.timestamp}>{formattedDate}</Text>
          </Card.Content>
        </Card>
      </TouchableOpacity>
    );
  };
  
  return (
    <SafeAreaView style={styles.container}>
      {/* Rename Dialog */}
      <Portal>
        <Dialog
          visible={renameDialogVisible}
          onDismiss={() => setRenameDialogVisible(false)}
          style={{ 
            backgroundColor: colors.card, 
            borderRadius: borderRadius.large,
            maxWidth: '90%',
            alignSelf: 'center',
            marginTop: -100 // Move the dialog up a bit from center
          }}
        >
          <Dialog.Title style={styles.dialogTitle}>Rename Conversation</Dialog.Title>
          <Dialog.Content>
            <TextInput
              label="Conversation Name"
              value={newTitle}
              onChangeText={setNewTitle}
              mode="outlined"
              style={styles.dialogInput}
              autoFocus
            />
          </Dialog.Content>
          <Dialog.Actions>
            <Button 
              onPress={() => setRenameDialogVisible(false)}
              style={styles.dialogButton}
            >
              Cancel
            </Button>
            <Button 
              onPress={handleRenameConversation} 
              mode="contained"
              style={styles.dialogButton}
              buttonColor={colors.primary}
            >
              Save
            </Button>
          </Dialog.Actions>
        </Dialog>
      </Portal>
      
      {/* Quick Menu Component */}
      <QuickMenu
        visible={menuVisible}
        onDismiss={() => {
          setMenuVisible(false);
          setSelectedConversation(null);
        }}
        options={[
          {
            id: 'rename',
            title: 'Rename',
            icon: 'pencil',
            onPress: () => {
              if (selectedConversation) {
                setNewTitle(selectedConversation.title);
                setRenameDialogVisible(true);
              }
            }
          },
          {
            id: 'delete',
            title: 'Delete',
            icon: 'delete',
            color: colors.error,
            onPress: () => {
              if (selectedConversation) {
                Alert.alert(
                  "Delete Conversation",
                  "Are you sure you want to delete this conversation?",
                  [
                    { text: "Cancel", style: "cancel" },
                    { 
                      text: "Delete", 
                      style: "destructive",
                      onPress: async () => {
                        try {
                          await conversationApi.delete(selectedConversation.id);
                          
                          // Update state after successful deletion
                          const updatedConversations = conversations.filter(
                            c => c.id !== selectedConversation.id
                          );
                          setConversations(updatedConversations);
                          
                          if (searchQuery) {
                            const updatedFiltered = filteredConversations.filter(
                              c => c.id !== selectedConversation.id
                            );
                            setFilteredConversations(updatedFiltered);
                          }
                        } catch (error) {
                          console.error('Error deleting conversation:', error);
                          Alert.alert('Error', 'Failed to delete conversation');
                        }
                      }
                    }
                  ]
                );
              }
            }
          }
        ]}
        position={menuPosition}
      />
      
      <View style={styles.searchContainer}>
        <TextInput
          placeholder="Search Conversations"
          value={searchQuery}
          onChangeText={setSearchQuery}
          mode="outlined"
          style={styles.searchBar}
          right={<TextInput.Icon name="magnify" />}
        />
      </View>
      
      {loading ? (
        <View style={styles.centered}>
          <ActivityIndicator animating={true} color={colors.primary} />
        </View>
      ) : error ? (
        <View style={styles.centered}>
          <Text style={styles.errorText}>{error}</Text>
          <Button 
            onPress={fetchConversations} 
            mode="contained"
            style={styles.retryButton}
            buttonColor={colors.primary}
          >
            Retry
          </Button>
        </View>
      ) : filteredConversations.length === 0 ? (
        <View style={styles.emptyContainer}>
          <Text style={styles.emptyText}>No Conversations Found</Text>
          <Text style={styles.emptySubtext}>
            Try adjusting your search or create a new conversation.
          </Text>
        </View>
      ) : (
        <FlatList
          data={filteredConversations}
          renderItem={renderConversationItem}
          keyExtractor={item => item.id.toString()}
          contentContainerStyle={styles.listContent}
          showsVerticalScrollIndicator={false}
        />
      )}
      
      {/* Floating Action Button */}
      <FAB
        icon="plus"
        style={styles.fab}
        onPress={() => {
          // Create a new conversation with a default title
          const newConversation = {
            title: "New Conversation",
            context: "",
            messages: []
          };
          
          // Call the API to create a new conversation
          conversationApi.create(newConversation)
            .then(response => {
              // Navigate to the new conversation
              navigation.navigate('ConversationDetail', { 
                conversationId: response.data.conversation.id,
                title: response.data.conversation.title,
                isNew: true
              });
            })
            .catch(error => {
              console.error('Error creating conversation:', error);
              Alert.alert('Error', 'Failed to create new conversation');
            });
        }}
      />
    </SafeAreaView>
  );
};

export default ConversationListScreen;
